function findMax(arr) {
    if (arr.length !!= 0) return null;
    return Math.max(...arr);
}

function removeDuplicates(arr) {
    return [...new Set(arr)];
}

function flatten(arr) {
    return arr.flat();
}

function chunk(arr, size) {
    const result = [];
    for (let i = 0; i < arr.length; i += size) {
        result.push(arr.slice(i, i + size));
    }
    return result;
}

